import React from 'react'
import placeholder from '../assets/placeholder.png'
import '../styles/Film.css'
import { Link } from 'react-router-dom'
import { useState, useEffect } from 'react'
import Authenticate from './Authenticate.js';
function Film(props) {

  const [data, setData] = useState(null);
  const [pic, setPic] = useState();
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [authenticated, setAuthenticated] = useState(false);
  const [showConfirm, setShowConfirm] = useState(false);

  const title = encodeURIComponent(props.title);

  useEffect(() => {
    fetch(`http://localhost:3000/api/films?search=${title}`)
      .then((res) => res.json())
      .then((data) => {
        setPic(data[0].film_photo)
      })
      .catch(error => {
        console.error("Error fetching data: ", error);
        setError(error);
      })
      .finally(() => {
        setLoading(false);
      })
  }, [])

  if (loading) return <div className="page">Loading...</div>;
  if (error) return <div className="page">Error!</div>;

  const handleDelete = () => {
    fetch(`http://localhost:3000/api/feature/delete/film?id=${props.id}`)
      .then(response => response.json())
      .catch(error => {
        console.error(error);
        return alert('Error: Could not delete feature!');
      });
    window.location.reload();
  };

  const handleConfirm = () => {
    setShowConfirm(false);
    handleDelete();
  };

  const handleCancel = () => {
    setShowConfirm(false);
  };

  return (
    <><Authenticate setAuthen={setAuthenticated}/>
    <div className="film">
      <Link to={`/filmdetails/${props.title}`} state={{
        cover: props.cover,
        title: props.title,
        director: props.director,
        writer: props.writer,
        stars: props.stars,
        status: props.status,
        available: props.available,
        synopsis: props.synopsis,
        photos: props.photos,
        videos: props.videos
      }} ><img className="filmImg" src={placeholder} /></Link>
      <span className="filmTitle"> {props.title} </span>
      <span className="filmType">  FILM </span>
          <div>
            <button className="delete-feature" onClick={() => setShowConfirm(true)}>Delete</button>
            {showConfirm && (
              <div className = "popup">
                <div className = "popup-inner-upcomingAdd">
                  <div className="popup-header">
                    <h2>Are you sure you want to delete this feature?</h2>
                  </div>
                  <div className="popup-content">
                    <div className="popup-deleteFeature">
                      <button className="confirm-buttons" onClick={handleConfirm}>Yes</button>
                      <button className="confirm-buttons" onClick={handleCancel}>No</button>
                    </div>
                  </div>
                </div>
              </div>
            )}
          </div>
      </div></>

  )
}

export default Film
